mapper.insert:
INSERT INTO participate (is_registration, is_participate, is_subscribe, event_id, user_id)
SELECT true, false, false, #{eventId}, #{userId}
FROM dual
WHERE NOT EXISTS (
    SELECT 1
    FROM participate
    WHERE event_id = #{eventId} AND user_id = #{userId}
)

mapper.update:
UPDATE participate
SET is_subscribe = #{isSubscribe}
WHERE event_id = #{eventId} AND user_id = #{userId}
