mapper.select:
select u.* from permission p
join user u on u.id = p.user_id
where event_id=#{eventId}

select event_id
from permission
where user_id = #{userId}

mapper.update:
update permission
set all_method_name = #{all_method_name},
    gmt_update = #{update_time,jdbcType=TIMESTAMP}
where user_id = #{user_id} and event_id = #{event_id}
